            >
              <LogOut className="text-red-600 mr-3 h-5 w-5" />
              <div className="text-left">
                <div>Logout</div>
                <div className="text-xs text-red-500">Sign out safely</div>
              </div>
            </Button>
          </nav>
        </div>
      </div>
    </>
  );
}

