  return (
    <div className="relative z-10 flex-shrink-0 flex h-16 bg-white/80 backdrop-blur-md shadow-lg border-b border-gray-200/50 lg:ml-0 ml-0">
      <div className="flex-1 px-6 flex justify-between">
