);

interface Contact {
  id: string;
  firstName: string;
  lastName: string;
  phones: string[];
  email: string;
  company: string;
  services: string;
  address?: string;
  qrCodeUrl?: string; // Added QR Code URL field
  groupIds: string[];
  createdAt: Date;
  ownerId: string;
}

interface Group {
  id: string;
  name: string;
  ownerId: string;
}

interface Template {
  id: string;
  name: string;
  content: string;
  createdAt: Date;
  ownerId: string;
}

function Contacts() {
  const { user } = useAuth();
  const { toast } = useToast();
  const [contacts, setContacts] = useState<Contact[]>([]);
  const [groups, setGroups] = useState<Group[]>([]);
  const [templates, setTemplates] = useState<Template[]>([]);
  const [filteredContacts, setFilteredContacts] = useState<Contact[]>([]);
  const [searchTerm, setSearchTerm] = useState("");
  const [selectedGroup, setSelectedGroup] = useState("all");
  const [viewMode, setViewMode] = useState<"grid" | "table">("grid");
  const [showAddModal, setShowAddModal] = useState(false);
  const [loading, setLoading] = useState(true);
  const [saving, setSaving] = useState(false);
  const [exporting, setExporting] = useState(false);
  const [importing, setImporting] = useState(false);
  const [editingContact, setEditingContact] = useState<Contact | null>(null);
  const [showEditModal, setShowEditModal] = useState(false);
  const [showGroupModal, setShowGroupModal] = useState(false);
  const [showTemplateModal, setShowTemplateModal] = useState(false);
  const [showImportModal, setShowImportModal] = useState(false);
  const [selectedContactForGroup, setSelectedContactForGroup] = useState<Contact | null>(null);
  const [selectedContactForMessage, setSelectedContactForMessage] = useState<Contact | null>(null);
  const [selectedTemplate, setSelectedTemplate] = useState<Template | null>(null);
  const [customMessage, setCustomMessage] = useState("");
  const [importFile, setImportFile] = useState<File | null>(null);
  const [importPreview, setImportPreview] = useState<any[]>([]);
  const [formData, setFormData] = useState<{
    firstName: string;
    lastName: string;
    phones: string[];
    email: string;
    company: string;
    services: string;
    address: string;
    qrCodeUrl: string; // Added QR Code URL field
    groupIds: string[];
  }>({
    firstName: "",
    lastName: "",
    phones: [],
    email: "",
    company: "",
    services: "",
    address: "",
    qrCodeUrl: "", // Added QR Code URL field
    groupIds: [],
  });

  useEffect(() => {
    const initializeContacts = async () => {
      try {
        setLoading(true);
        if (user) {
          await fetchData();
        }
      } catch (error) {
        console.error("Error initializing contacts:", error);
        toast({
          title: "Error",
          description: "Failed to initialize contacts",
          variant: "destructive",
        });
      } finally {
        setLoading(false);
      }
    };

    initializeContacts();
  }, [user]);

  useEffect(() => {
    let filtered = contacts;

    if (searchTerm) {
      filtered = filtered.filter(contact => 
        contact.firstName?.toLowerCase().includes(searchTerm.toLowerCase()) ||
        contact.lastName?.toLowerCase().includes(searchTerm.toLowerCase()) ||
        contact.email?.toLowerCase().includes(searchTerm.toLowerCase()) ||
        contact.phones?.some(p => p.includes(searchTerm)) ||
        contact.company?.toLowerCase().includes(searchTerm.toLowerCase())
      );
    }

    if (selectedGroup !== "all") {
      filtered = filtered.filter(contact => 
        contact.groupIds?.includes(selectedGroup)
      );
    }

    setFilteredContacts(filtered);
  }, [contacts, searchTerm, selectedGroup]);

  const fetchData = async () => {
    if (!user) {
      setLoading(false);
      return;
    }

    try {
      // Fetch contacts
      const contactsQuery = query(
        collection(db, "contacts"), 
        where("ownerId", "==", user.uid)
      );
      const contactsSnapshot = await getDocs(contactsQuery);
      const contactsData = contactsSnapshot.docs.map(doc => {
        const data = doc.data() as any;
        const contact: Contact = {
          id: doc.id,
          firstName: data.firstName || "",
          lastName: data.lastName || "",
          phones: [],
          email: data.email || "",
          company: data.company || "",
          services: data.services || "",
          address: data.address || "",
          qrCodeUrl: data.qrCodeUrl || "", // Added QR Code URL field
          groupIds: data.groupIds || [],
          createdAt: data.createdAt?.toDate() || new Date(),
          ownerId: data.ownerId,
        };

        if (Array.isArray(data.phones)) {
          contact.phones = data.phones;
        } else {
          const phoneNumbers = [];
          if (data.phone) phoneNumbers.push(data.phone);
          if (data.phone2) phoneNumbers.push(data.phone2);
          contact.phones = phoneNumbers;
        }

        return contact;
      });

      // Fetch groups
      const groupsQuery = query(
        collection(db, "groups"), 
        where("ownerId", "==", user.uid)
      );
      const groupsSnapshot = await getDocs(groupsQuery);
      const groupsData = groupsSnapshot.docs.map(doc => ({
        id: doc.id,
        ...doc.data(),
      })) as Group[];

      // Fetch templates
      const templatesQuery = query(
        collection(db, "templates"), 
        where("ownerId", "==", user.uid)
      );
      const templatesSnapshot = await getDocs(templatesQuery);
      const templatesData = templatesSnapshot.docs.map(doc => ({
        id: doc.id,
        ...doc.data(),
        createdAt: doc.data().createdAt?.toDate() || new Date(),
      })) as Template[];

      setContacts(contactsData);
      setGroups(groupsData);
      setTemplates(templatesData);
    } catch (error) {
      console.error("Error fetching data:", error);
      toast({
        title: "Error",
        description: "Failed to fetch data",
        variant: "destructive",
      });
    }
