                        <Heart className="h-4 w-4 mr-1" />
                        Client Testimonials
                      </Label>
                      <Textarea
                        id="testimonials"
                        value={digitalCard.testimonials}
                        onChange={(e) => handleFieldChange('testimonials', e.target.value)}
                        rows={3}
                        className="border-gray-300 focus:border-purple-500 focus:ring-purple-500"
                        placeholder="Share what clients say about your work..."
                      />
                    </div>

                    {/* QR Code Style Selector */}
                    <div className="sm:col-span-2">
                      <Label className="flex items-center text-sm font-medium text-gray-700 mb-3">
                        <QrCode className="h-4 w-4 mr-1" />
                        QR Code Style
                      </Label>
                      <div className="grid grid-cols-3 gap-3">
                        {QR_STYLES.map((style) => (
                          <div
                            key={style.id}
                            className={`cursor-pointer rounded-lg p-3 border-2 transition-all duration-200 hover:scale-105 ${
                              digitalCard.qrStyle === style.id
                                ? 'border-purple-500 bg-purple-50 shadow-md'
                                : 'border-gray-200 hover:border-gray-300'
                            }`}
                            onClick={() => setDigitalCard(prev => ({ ...prev, qrStyle: style.id }))}
                          >
                            <div className="flex flex-col items-center text-center">
                              <style.icon className="h-6 w-6 mb-2 text-purple-600" />
                              <p className="text-xs font-medium text-gray-700">{style.name}</p>
                              <p className="text-xs text-gray-500 mt-1">{style.description}</p>
                            </div>
                          </div>
                        ))}
                      </div>
                    </div>
                  </div>

                  {/* Action Buttons */}
                  <div className="mt-8 flex flex-col sm:flex-row sm:justify-between gap-4">
                    <div className="flex space-x-3">
                      <Button 
                        onClick={handleSaveProfile} 
                        disabled={saving}
                        className="bg-gradient-to-r from-blue-600 to-purple-600 hover:from-blue-700 hover:to-purple-700 text-white"
                      >
                        <Save className="h-4 w-4 mr-2" />
                        {saving ? "Saving..." : "Save Profile"}
                      </Button>
                      <Button variant="outline" onClick={handleDownloadVCard} className="border-purple-300 text-purple-700 hover:bg-purple-50">
                        <Download className="h-4 w-4 mr-2" />
                        Download vCard
                      </Button>
                    </div>
                    <div className="flex space-x-3">
                      <Button variant="outline" onClick={handleDownloadQR} className="border-green-300 text-green-700 hover:bg-green-50">
                        <QrCode className="h-4 w-4 mr-2" />
                        Download QR
                      </Button>
                      <Button variant="outline" onClick={handleCopyShareLink} className="border-blue-300 text-blue-700 hover:bg-blue-50">
                        <LinkIcon className="h-4 w-4 mr-2" />
                        Copy Link
                      </Button>
                    </div>
                  </div>
                </CardContent>
              </Card>
            </div>
          </div>

          {/* Live Preview Section - Fixed and Properly Sized */}
          <div className="mt-8 lg:mt-0 lg:col-span-5">
            <div className="lg:sticky lg:top-6">
              <Card className="shadow-2xl border-0 bg-white/90 backdrop-blur">
                <CardContent className="p-6">
                  <div className="flex items-center justify-between mb-6">
                    <h3 className="text-lg font-semibold text-gray-900 flex items-center">
                      <Zap className="h-5 w-5 mr-2 text-yellow-600" />
                      Live Preview
                    </h3>
                    <div className="flex items-center space-x-2 text-sm text-gray-500">
                      <div className="w-2 h-2 bg-green-400 rounded-full animate-pulse"></div>
                      <span>Live</span>
                    </div>
                  </div>

                  {/* Preview Container with proper sizing */}
                  <div className="flex items-center justify-center min-h-[500px] max-h-[600px] overflow-auto bg-gradient-to-br from-gray-50 to-gray-100 rounded-lg p-4">
                    <div className="w-full max-w-xs">
                      {renderCardPreview()}
                    </div>
                  </div>

                  {/* Template Info */}
                  <div className="mt-6 p-4 bg-gradient-to-r from-gray-50 to-gray-100 rounded-lg border">
                    <div className="flex items-center justify-between">
                      <div>
                        <p className="text-sm font-medium text-gray-900">
                          {TEMPLATES.find(t => t.id === digitalCard.template)?.name} Template
                        </p>
                        <p className="text-xs text-gray-500">
                          {TEMPLATES.find(t => t.id === digitalCard.template)?.description}
                        </p>
                      </div>
                      <div className="flex items-center space-x-2">
                        <div className="text-2xl">
                          {TEMPLATES.find(t => t.id === digitalCard.template)?.preview}
                        </div>
                        {(() => {
                          const template = TEMPLATES.find(t => t.id === digitalCard.template);
                          const IconComponent = template?.icon;
                          return IconComponent ? <IconComponent className="h-5 w-5 text-purple-600" /> : null;
                        })()}
                      </div>
                    </div>

                    {/* Color Display */}
                    <div className="flex items-center space-x-2 mt-3">
                      <span className="text-xs text-gray-500">Colors:</span>
                      <div 
                        className="w-4 h-4 rounded-full border border-gray-300"
                        style={{ backgroundColor: digitalCard.primaryColor }}
                      />
                      <div 
                        className="w-4 h-4 rounded-full border border-gray-300"
                        style={{ backgroundColor: digitalCard.secondaryColor }}
                      />
                    </div>
                  </div>
                </CardContent>
              </Card>
            </div>
          </div>
        </div>
      </div>

      {/* Usage Limit Modal */}
      <UsageLimitModal
        isOpen={showLimitModal}
        onClose={() => setShowLimitModal(false)}
        feature="digitalCard"
        currentCount={usage.digitalCardsCount}
        limit={limits.digitalCards}
      />
    </div>
  );
}

